settings.custom_css:
.img-with-border img{
border:1px solid black;
padding-bottom: 0;
}
.rounded-img{
margin: 0 auto;
}
.rounded-img img{
border-radius: 50% !important;
}

.make-header-big .slide__block-subtitle.text-xl{
    font-size: 2.25rem;
    line-height: 3.75rem;
font-weight: 700;
}

.make-header-big .sf__slide-content.max-w-4xl{
max-width: 54rem !important;
}

.custom-content-1 .section__heading{
color: #9B93D4;
font-weight: bold !important;
}

.custom-content-1 .section__header{
margin-bottom: 25px !important;
}

.featured-product .prod__dynamic_checkout{
margin-top: 24px !important;
}
.featured-product .add-to-cart{
background-color: #b5c347 !important;
border:none;
}

.featured-product span.not-change.atc-text{
font-size: 0;
}
.featured-product span.not-change.atc-text:after{
    content: 'Buy Now';
    color: #fff;
    font-size: 1.4rem;
    vertical-align: middle;
}

.add-to-cart.sf__btn:hover,
.sf-custom__block-inner .add-to-cart.sf__btn:hover{
background-color: #b5c347 !important;
}

.featured-product .prod__option-label__quantity,
.featured-product .prod__option-label{
display: none !important;
}

.featured-1 .swiper-button-next,
.featured-1 .swiper-button-prev{
display: block !important;
cursor: pointer;
padding: 10px;
pointer-events: auto !important;
}

.highlight-green{
    color: #B5C347;
}
.how-to-get-text h4{
font-weight: bold;
}
.how-to-get-text p{
font-size: 1.3rem;
}
@media screen and (max-width: 1024px){
.hide-on-mobile{
display: none !important;
}
}

@media screen and (min-width: 1024px){
.hide-on-desktop{
display: none !important;
}
}
